	private final String name;
	private final WidgetInfo info;
	private static final Map<String, WidgetInfo> map;

	static
	{
		ImmutableMap.Builder<String, WidgetInfo> builder = ImmutableMap.builder();

		for (TabMap tabs : values())
		{
			builder.put(tabs.getName(), tabs.getInfo());
		}

		map = builder.build();
	}

	@Nullable
	public static WidgetInfo getWidget(String tab)
	{
		return map.getOrDefault(tab, null);
	}
